// (2) 箭头函数
import React from 'react'
const [count, setCount] = React.useState(0);

function Button() {
  count++
  console.log(count)
};

// 2. 使用组件
function App() {

  return (
    <div>
      <button onClick={Button}>{count}</button>
    </div>
  );
}

export default App;
